	if (value > (address(this).balance - 0.01 ether)) {
		if (lastRound)
			selfdestruct(admin);
		emit LogRoundEnd(msg.sender, value, address(this).balance, round);
		globalReinitialization();
		round++;
		return;
	}
	to.transfer(value);
