    bool public unpausePending = false;

    modifier onlyRequester() {
        require(msg.sender == requester);
        _;
    }
    function requestPause() public onlyRequester {
        pausePending = true;
        unpausePending = false;
    }

    function requestUnpause() public onlyRequester {
        pausePending = false;
        unpausePending = true;
